(6657, 1271, 'Info4local.gov.uk', 'Information for local government from central government.  This site gives local authorities a quick and easy way to find relevant information on the web sites of central government departments, agencies and public bodies. It includes extensive information on implementing electronic government.', 'http://www.info4local.gov.uk', 'http://www.info4local.gov.uk/?view=Search+results&subjects=all&departmentsIds=0&informationTypesIds=all&query={$formKeywords}', NULL, 3),
(6656, 1271, 'Directgov', 'Directgov is a government service giving people access to the latest and widest range of public service information in one place on the Web and on Digital TV. Directgov uses the specifications formulated and consulted on through UK GovTalk.', 'http://www.direct.gov.uk', 'http://www.direct.gov.uk/AdvancedSearch/SearchResults/fs/en?NP=1&PO1=C&PI1=W&PF1=A&PG=1&RP=20&SC=__dgov_site&Z=1&PT1={$formKeywords}', NULL, 2),
(6655, 1271, 'Government of Canada Publicatons', 'The Government of Canada Publications Web site provides a single window access to free and priced publications authored by Government of Canada departments. The database does not have every publication published from all departments. It does however, have over 100,000 publications listed and this number is increasing on a daily basis as this site continues to collaborate with author departments.', 'http://www.canada.gc.ca/main_e.html', 'http://publications.gc.ca/control/quickPublicSearch?searchAction=2&termValue={$formKeywords}', NULL, 1),
(6653, 1270, 'Space Science Education Home Page -- Goddard Space Flight Center', 'Data from several NASA missions, student activities, teacher resources', 'http://gsfc.nasa.gov/education/education_home.html', 'http://search.nasa.gov/nasasearch/search/search.jsp?nasaInclude={$formKeywords}', NULL, 2),
(6654, 1271, 'Access to Australian Government Information and Services', 'The Australian Government Entry Point offers comprehensive and integrated access to Australian Government information and services.  You will find a concentrated body of Australian Government information through this site. <a href=\"http://www.australia.gov.au\">www.australia.gov.au</a> currently signposts over 700 Australian Government web sites, indexes more than 5,000,000 pages and uses both metadata and full text indexing to ensure it is a comprehensive government portal.', 'http://www.fed.gov.au', 'http://govsearch.australia.gov.au/search/search.cgi?collection=gov&form=au&query=&query_phrase=&query_or=&query_not=&meta_f_sand=&scope=&fscope=512&num_ranks=20&chksummary=chksummary&query_and={$formKeywords}', NULL, 0),
(6652, 1270, 'Science, Mathematics, Engineering and Technology Education (SMETE)', 'Science, Mathematics, Engineering and Technology Education (SMETE) contains a searchable working prototype of a National Science, Mathematics, Engineering, and Technology Education Digital Library.', 'http://www.smete.org/', 'http://www.smete.org/smete/?path=/public/find/search_results.jhtml&_DARGS=/smete/public/find/index_body.jhtml&/smete/forms/FindLearningObjects.operation=search&_D:/smete/forms/FindLearningObjects.operation=%20&_D:/smete/forms/FindLearningObjects.keyword=%20&/smete/forms/FindLearningObjects.learningResourceType=&_D:/smete/forms/FindLearningObjects.learningResourceType=%20&/smete/forms/FindLearningObjects.grade=0-Any&_D:/smete/forms/FindLearningObjects.grade=%20&/smete/forms/FindLearningObjects.title=&_D:/smete/forms/FindLearningObjects.title=%20&/smete/forms/FindLearningObjects.author=&_D:/smete/forms/FindLearningObjects.author=%20&/smete/forms/FindLearningObjects.hostCollection=&_D:/smete/forms/FindLearningObjects.hostCollection=%20&/smete/forms/FindLearningObjects.afterYear=&_D:/smete/forms/FindLearningObjects.afterYear=%20&/smete/forms/FindLearningObjects.beforeYear=&_D:/smete/forms/FindLearningObjects.beforeYear=%20&&/smete/forms/FindLearningObjects.keyword={$formKeywords}', NULL, 1),
