(5312, 1013, 'The ArchNewsNow', 'The ArchNewsNow (ANN) newsletter, launched in February 2002, is delivered daily to subscribers -- free of charge -- via e-mail by 9:30AM (New York time). It hyperlinks directly to the latest news and commentary gleaned from sources around the world.', 'http://www.archnewsnow.com/', 'http://www.archnewsnow.com/cgi-local/search.pl?Required={$formKeywords}', NULL, 10),
(5309, 1013, 'The Moscow Times', 'The Moscow Times, Russia (1994-)', 'http://www.moscowtimes.ru/doc/Search.html', 'http://www.moscowtimes.ru/cgi-bin/search?config=&exclude=&method=and&format=long&sort=score&matchesperpage=10&words={$formKeywords}', NULL, 7),
(5310, 1013, 'Washington Post', 'Washington Post, Washington, DC (last two weeks)', 'http://www.washingtonpost.com/', 'http://www.washingtonpost.com/cgi-bin/search99.pl?searchsection=news&searchdatabase=news&x=11&y=8&searchtext={$formKeywords}', NULL, 8),
(5311, 1013, 'Newsdirectory', 'Newsdirectory is a comprehensive and searchable guide to the world\'s English-language online media. Currently over 8,000 newspapers and magazines are listed, as well as more than 1,000 U.S. television broadcasters.', 'http://www.newsdirectory.com/', 'http://ask.elibrary.com/search.asp?refid=NEWSD&datetype=all&srcmags=checked&srcnews=checked&srcbooks=checked&srctvrad=checked&query={$formKeywords}', NULL, 9),
(5308, 1013, 'The Japan Times Online', 'The Japan Times Online, Japan (January 1999-)', 'http://www.japantimes.co.jp/', 'http://www.google.co.jp/custom?domains=japantimes.co.jp&client=pub-4223870936880387&forid=1&ie=Shift_JIS&oe=Shift_JIS&term1=&cof=GALT%3A%23008000%3BGL%3A1%3BDIV%3A%23336699%3BVLC%3A663399%3BAH%3Acenter%3BBGC%3AFFFFFF%3BLBGC%3AFFFFFF%3BALC%3A0000FF%3BLC%3A0000FF%3BT%3A000000%3BGFNT%3A0000FF%3BGIMP%3A0000FF%3BLH%3A60%3BLW%3A200%3BL%3Ahttp%3A%2F%2Fwww.japantimes.co.jp%2Fimages%2Fheader_title.gif%3BS%3Ahttp%3A%2F%2Fwww.japantimes.co.jp%2F%3BFORID%3A1%3B&hl=ja&advancesearch=&q={$formKeywords}', NULL, 6),
