(5305, 1013, 'Mail & Guardian Newspaper', 'Mail & Guardian Newspaper, South Africa (1994-)', 'http://www.mg.co.za/', 'http://www.mg.co.za/mg_search_results.aspx?PrintEdition=PrintEdition&DailyNews=DailyNews&SearchSection=&StartDay=&StartMonth=&StartYear=&EndDay=&EndMonth=&EndYear=&keywords={$formKeywords}', NULL, 3),
(5302, 1013, 'Google News', 'Search news reports using the popular search engine Google\'s news feature.', 'http://news.google.com/', 'http://news.google.com/news?hl=en&q={$formKeywords}', NULL, 0),
(5303, 1013, 'Globe and Mail', 'Globe and Mail, Toronto (last seven days)', 'http://www.globeandmail.com/', 'http://www.globeandmail.com/servlet/HTMLTemplate/search?tf=tgam/search/tgam/SearchResults.html&cf=tgam/search/tgam/SearchResults.cfg&current_row=1&start_row=1&num_rows=10&keywords={$formKeywords}', NULL, 1),
(5301, 1012, 'Architronic', 'Architronic is a scholarly refereed journal, exploring the new ranges of architectural communication available through digital media. It is a platform for both presenting and reviewing research as a journal, and a forum for stimulating dialogue on emerging ideas.', 'http://architronic.saed.kent.edu/', 'http://www.google.com/search?q=site:architronic.saed.kent.edu+', NULL, 2),
(5300, 1012, 'Aesthetics On-line', 'In Aesthetics On-line you\'ll find articles about aesthetics, philosophy of art, art theory and art criticism, as well as information about aesthetics events worldwide, and links to other aesthetics-related resources on the internet, including the Aesthetics-L email discussion list.', 'http://www.aesthetics-online.org/', 'http://www.aesthetics-online.org/tech/search.cgi?context=Site&terms={$formKeywords}', NULL, 1),
(5299, 1012, 'Arts Journal', 'Arts Journal is a weekday digest of some of the best arts and cultural journalism. Each day Arts Journal combs through more than 100 English-language newspapers, magazines and publications featuring writing about arts and culture. Direct links to the most interesting or important stories are posted every weekday on the Arts Journal news pages. Stories from sites that charge for access are excluded as are sites which require visitors to register, with the exception of the New York Times.', 'http://www.artsjournal.com/', 'http://www.google.com/custom?domains=artsjournal.com&sitesearch=artsjournal.com&sa=GoogleSearch&cof=LW:240;L:http://www.artsjournal.com/img/logo.gif;LH:52;AH:left;S:http://www.artsjournal.com;AWFID:edabed9eb3afda60;&q={$formKeywords}', NULL, 0),
(5298, 1011, 'ArtsEdNet', 'ArtsEdNet, an online service developed by the Getty Education Institute for the Arts, supports the needs of the K-12 arts education community. It focuses on helping arts educators, general classroom teachers using the arts in their curriculum, museum educators, and university', 'http://www.getty.edu/artsednet/home.html', 'http://www.google.com/search?hl=en&btnG=Search&q=site%3Awww.getty.edu%2Feducation%2F+{$formKeywords}', NULL, 1),
(5297, 1011, 'ARTSEDGE', 'ARTSEDGE supports the place of arts education at the center of the curriculum through the creative and appropriate uses of technology. ARTSEDGE helps educators to teach in, through and about the arts.', 'http://artsedge.kennedy-center.org/', 'http://artsedge.kennedy-center.org/search/search_perform.cfm?advanced=false&contentTypes=&artsSubjects=&x=31&y=9&keywords={$formKeywords}', NULL, 0);
