(5039, 960, 'SPIRES', 'SPIRES provides search more than 450,000 high-energy physics related articles, including journal papers, preprints, e-prints, technical reports, conference papers and theses.', 'http://www.slac.stanford.edu/spires/hep/', 'http://www.slac.stanford.edu/spires/find/hep/www?TITLE=&C=&REPORT-NUM=&AFFILIATION=&cn=&k=&cc=&eprint=+&eprint=&topcit=&url=&J=+&*=&ps=+&DATE=&*=+&FORMAT=WWW&SEQUENCE=&AUTHOR={$formKeywords}', NULL, 9),
(5037, 960, 'NASA Technical Reports Server', 'NASA Technical Reports Server allows users to search available online NASA published documents, including meeting presentations, journal articles, conference proceedings, and technical reports. Many documents are available in compressed PostScript and PDF formats. All documents are unclassified and publicly available.', 'http://ntrs.nasa.gov/index.jsp?method=aboutntrs', 'http://ntrs.nasa.gov/search.jsp?N=0&Ntk=all&Ntx=mode%20matchall&Ntt={$formKeywords}', NULL, 7),
